export const CECI_OPEN_MENU = 'ceci/CECI_OPEN_MENU'
export const CECI_CLOSE_MENU = 'ceci/CECI_CLOSE_MENU'
export const CECI_TOGGLE_MENU = 'ceci/CECI_TOGGLE_MENU'
export const CECI_LISTEN_ON = 'ceci/CECI_LISTEN_ON'
export const CECI_LISTEN_OFF = 'ceci/CECI_LISTEN_OFF'
export const CECI_ADD_STT = 'ceci/CECI_ADD_STT'
export const CECI_ADD_PRODUCTS = 'ceci/CECI_ADD_PRODUTS'

let TTSVoice = {}

window.speechSynthesis.onvoiceschanged = () => {
  TTSVoice = window.speechSynthesis.getVoices()[31]
}

const initialState = {
  ceciMenu: false,
  ceciListening: false,
  ceciSTT: [],
  hasResults: true,
  showBig: false,
  results: []
}

export default (state = initialState, action) => {
  switch (action.type) {
    case CECI_OPEN_MENU:
      return {
        ...state,
        ceciMenu: true,
      }
    case CECI_CLOSE_MENU:
      return {
        ...state,
        ceciMenu: false
      }
    case CECI_TOGGLE_MENU:
      return {
        ...state,
        ceciMenu: !state.ceciMenu
      }
    case CECI_LISTEN_ON:
      return {
        ...state,
        ceciListening: true
      }
    case CECI_LISTEN_OFF:
      return {
        ...state,
        ceciListening: false
      }
    case CECI_ADD_STT:
      return {
        ...state,
        ceciSTT: [
          action.STT,
          ...state.ceciSTT
        ]
      }
    case CECI_ADD_PRODUCTS:
      return {
        ...state,
        results: action.results
      }
    default:
      return state
  }
}

export const ceci_open = () => (
  dispatch => {
    dispatch({
      type: CECI_OPEN_MENU
    })
    return dispatch(ceci_listen_on())
  }
)

export const ceci_close = () => (
  dispatch => {
    dispatch(ceci_listen_off())
    dispatch({
      type: CECI_CLOSE_MENU
    })
  }
)

export const ceci_toggle = () => (
  dispatch => dispatch({
    type: CECI_TOGGLE_MENU
  })
)

export const ceci_listen_on = () => (
  dispatch => {
    return setTimeout(() => {
      dispatch({
        type: CECI_LISTEN_ON
      })
      dispatch(capture_speech())
      beepSound.play()
    }, 500)
  }
)

export const ceci_listen_off = () => (
  dispatch => dispatch({
    type: CECI_LISTEN_OFF
  })
)

export const ceci_add_stt = (STT) => (
  dispatch => {
    dispatch({
      type: CECI_ADD_STT,
      STT
    })
    dispatch(sayText(STT))
    return dispatch(ceci_close())
  }
)

export const sayText = (text) => dispatch => {
  const utterance = new SpeechSynthesisUtterance()
  utterance.text = text
  utterance.lang = 'es-cl'
  utterance.volume = 0.8;
  utterance.rate = 0.8;
  utterance.voice = TTSVoice
  window.speechSynthesis.speak(utterance)
}

const recognition = new (window.SpeechRecognition || window.webkitSpeechRecognition || window.mozSpeechRecognition || window.msSpeechRecognition)();
recognition.lang = 'es-cl';
recognition.interimResults = false;
recognition.maxAlternatives = 5;



export const capture_speech = () => (
  dispatch => {
    recognition.onresult = (event) => {
       //dispatch(ceci_add_stt(event.results[0][0].transcript))
       dispatch(send_text_to_api(event.results[0][0].transcript))
    };
    recognition.start()
  }
)

export const add_results = (results) => (
  dispatch => dispatch({
    type: CECI_ADD_PRODUCTS,
    results
  })
)

export const send_text_to_api = (text) => dispatch => {
  fetch('/instructions', {
    method: 'POST',
    headers: {
      "Content-Type": "application/json"
    },
    body: JSON.stringify({
      instruction: text
    })
  })
  .then(r => r.json())
  .then(r => {console.log(r); return r})
  .then(r => {dispatch(sayText(r.json.response)) ; return r})
  .then(r => {dispatch(add_results(r.json.data)); return r})
  .then(r => dispatch(ceci_close()))
}

const beepSound = new Audio("data:audio/wav;base64,")
